find . -type f -not -path "*/\.*" | sort > repo_files.txt && echo "FILE COUNT BY TYPE:" >> repo_report.txt && find . -type f -not -path "*/\.*" -name "*.*" | grep -o "\.[^\.]*$" | sort | uniq -c | sort -nr >> repo_report.txt && echo -e "\n\nDIRECTORY STRUCTURE:" >> repo_report.txt && find . -type d -not -path "*/\.*" | sort >> repo_report.txt && echo -e "\n\nREADME CONTENT:" >> repo_report.txt && find . -maxdepth 2 -name "README*" -exec cat {} \; >> repo_report.txt && echo -e "\n\nPYTHON FILES:" >> repo_report.txt && find . -name "*.py" | sort >> repo_report.txt && echo -e "\n\nTOP PYTHON MODULES:" >> repo_report.txt && find . -name "*.py" -exec wc -l {} \; | sort -nr | head -10 >> repo_report.txt && [ -d .git ] && echo -e "\n\nGIT INFO:" >> repo_report.txt && git log --pretty=format:"%h - %an, %ar : %s" -10 >> repo_report.txt && echo -e "\n\nBRANCHES:" >> repo_report.txt && git branch -a >> repo_report.txt && echo -e "\n\nReport generated on $(date)" >> repo_report.txt && echo "Repository report generated as repo_report.txt and repo_files.txt"